form.addEventListener('submit', (e) => {
    e.preventDefault();

    const formData = new FormData(form);

    // Log form values
    for (let [name, value] of formData) {
        console.log(`${name}: ${value}`);
    }

    // Submit via fetch
    fetch('/submit', {
        method: 'POST',
        body: formData
    });
});

