// PHP code goes here
echo "Hello World!";

// Variables start with $
$name = "John";
$age = 30;

// Constants
define("PI", 3.14159);

// Variables
$name = "Alice";  // String
$count = 15;      // Integer
$price = 4.99;    // Float
$is_active = true; // Boolean

// Variable variables
$$name = "Bob";   // Creates $Alice

// Output
echo "Hello $name";  // Hello Alice

// Arithmetic
$sum = 10 + 5;  // 15

// Comparison
$isEqual = (5 == '5');  // true (loose)
$isIdentical = (5 === '5'); // false (strict)

// String concatenation
$greeting = "Hello " . "World!";

// Ternary operator
$message = ($age >= 18) ? "Adult" : "Minor";

// This is a single-line comment

# This is also single-line

